import axios from "axios";


axios.defaults.baseURL = "http://localhost:3001";
axios.defaults.withCredentials = true;


export async function loginApi(email, password){


    await axios.post('/api/auth/login', {email, password})
        .then(res=>{
            if(res.status === 200){

                return res.data

            }
            else{
                return false
            }
        })
        .catch(error=>{
            console.log(error)
            return false
        })
}

export async function signupApi(firstName,lastName, email, password, imageUrl){


    await axios.post('/api/auth/signup', {firstName, lastName, email, password, imageUrl})
        .then(res=>{
            if(res.status === 200){

                return true

            }
            else{
                return false
            }
        })
        .catch(error=>{
            console.log(error)
            return false
        })
}

export async function signoutApi(){


    await axios.post('/api/auth/signout')
        .then(res=>{
            if(res.status === 200){

                return true

            }
            else{
                return false
            }
        })
        .catch(error=>{
            console.log(error)
            return false
        })
}
