import React, {useEffect, useMemo, useState} from 'react'

import {Main} from "./Main";
import {Aside} from "./Aside";
import {AddEventModal} from "../../../components/modals/AddEventModal";
import {SideOverlay} from "../../../components/overlays/SideOverlay";
import {getUserEvents} from "../../../api/user";
import axios from "axios";
import {useNavigate} from "react-router-dom";
import {Calendar} from "../../../components/calendar/Calendar";




export const Events = () => {


    const [open, setOpen] = useState(false)
    const [formType, setFormType] = useState(true)
    const [eventIdSelected, setEventIdSelected] = useState({})



    const handleAddEvent = (e) =>{

        setFormType(true)
        setOpen(true)

    }

    const handleViewEvent = async (e, eventId)=>{

        console.log("handle", eventId)

        await setEventIdSelected(eventId)

        setFormType(false)
        setOpen(true)


    }


    const getEventsFromServer = async () =>{


        const events = await axios.get('/api/user/get-user-events')
            .catch(error=>{
                console.log(error)

            })

        const eventsMap = new Map();

        await events.data.forEach(event=> {

            const day = event.date.slice(9,10)
            const month = event.date.slice(6,7)
            const key = `${day}-${month}`

            const prevArray = eventsMap.get(key)

            if(!prevArray){
                eventsMap.set(key, [event])
            }else{
                prevArray.push(event)

            }
        })

        return eventsMap

    }


    const events = useMemo(()=> getEventsFromServer())




    return (

        <>

            <SideOverlay open={open} setOpen={setOpen} formType={formType} eventId={eventIdSelected}  />

            <main className="lg:py-8 lg:px-16 px-4 flex-1 overflow-y-auto">

                <section aria-labelledby="primary-heading" className="min-w-0 flex-1 h-full flex flex-col lg:order-last">

                    <Calendar handleAddEvent={handleAddEvent} handleViewEvent={handleViewEvent} events={events} />

                </section>
            </main>
        </>


    )
}
