import React, {useEffect, useState} from 'react'
import {PieChart} from "../../../components/charts/PieChart";
import {useParams} from "react-router-dom";
import axios from "axios";

const optionsSet = {
    animationEnabled: true,
    exportEnabled: true,
    theme: "light1", // "light1", "dark1", "dark2"
    title:{
        text: "Live Votes"
    },
    data: [{
        type: "pie",
        indexLabel: "{label}: {y}%",
        startAngle: -90,
        dataPoints: [
            { y: 20, label: "Airfare" },
            { y: 24, label: "Food & Drinks" },
            { y: 20, label: "Accomodation" },
            { y: 14, label: "Transportation" },
            { y: 12, label: "Activities" },
            { y: 10, label: "Misc" }
        ]
    }]
}

const optionsSet2 = {
    animationEnabled: true,
    exportEnabled: true,
    theme: "light1", // "light1", "dark1", "dark2"
    title:{
        text: "Live Votes"
    },
    data: [{
        type: "pie",
        indexLabel: "{label}: {y}%",
        startAngle: -90,
        dataPoints: [

        ]
    }]
}

export const Live = () => {

    const [options, setOptions] = useState(optionsSet2)
    const {eventId} = useParams()

    useEffect(()=>{

        axios.get(`/api/public/get-event/${eventId}`)
            .then(res=>{

                res.data.genres.forEach(genre =>{
                    options.data.findOne({type: 'pie'})
                        .then(data=>{
                            data.dataPoints.push({
                                y: genre.counter,
                                label: genre.name
                            })
                        })
                        .catch(error=>console.log(error))
                })

                console.log(options)

            })
            .catch(error=>{
                console.log(error)
            })

    },[] )

    return (


        <main className="mt-5 lg:py-8 lg:px-16 px-4 flex-1 overflow-y-auto">
            <section aria-labelledby="primary-heading" className="min-w-0 flex-1 h-full flex flex-col lg:order-last">

            <PieChart options={options} />

            </section>
        </main>
    )
}
