import axios from "axios";

axios.defaults.baseURL = "http://localhost:3001";
axios.defaults.withCredentials = true;

export const signin = async (email, password) =>{



   await axios.post('/api/auth/login', {email, password})
       .then(res=>{
           if(res.status === 200){

               //set user

               return true
           }
           else{
               return false
           }
       })
       .catch(error=>{
           console.log(error)
           return false
       })

}
