/* This example requires Tailwind CSS v2.0+ */
import {Fragment, useEffect, useState} from 'react'
import { Disclosure, Menu, Transition } from '@headlessui/react'
import axios from "axios";
import {useParams} from "react-router-dom";


const people = [
    {
        name: 'Michael Foster',
        role: 'Co-Founder / CTO',
        imageUrl:
            'https://images.unsplash.com/photo-1519244703995-f4e0f30006d5?ixlib=rb-=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=facearea&facepad=8&w=1024&h=1024&q=80',
    },
    // More people...
]


function classNames(...classes) {
    return classes.filter(Boolean).join(' ')
}

export const Event = () => {

    const [event, setEvent] = useState({})

    const {eventId} = useParams()

    console.log(eventId)

    const getEvent = () =>{

        axios.get(`/api/public/get-event/${eventId}`)
            .then(res=>{
                setEvent(res.data)
            })
            .catch(error=>{
                console.log(error)
            })

    }

    useEffect(()=>{
        getEvent()
    },[])

    console.log(event)

    return (


            <div className="min-h-full">
                <div className="bg-white border-b border-gray-200 max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
                    <div className="flex justify-between h-16">
                        <div className="flex">
                            <div className="flex-shrink-0 flex items-center">
                                <img
                                    className=" h-8 w-auto"
                                    src="https://tailwindui.com/img/logos/workflow-logo-indigo-600-mark-gray-800-text.svg"
                                    alt="Workflow"
                                />
                            </div>

                        </div>
                    </div>
                </div>

                <div className="py-10">

                    <main>
                        <div className="max-w-7xl mx-auto sm:px-6 lg:px-8">
                            {/* Replace with your content */}
                            <div className=" px-4 py-8 sm:px-0">
                                <div className="space-y-8 sm:space-y-12">
                                    <div className="space-y-5 sm:mx-auto sm:max-w-xl sm:space-y-4 lg:max-w-5xl">
                                        <h2 className="text-center text-3xl font-extrabold tracking-tight sm:text-4xl">{event.name}</h2>
                                        <div className=" flex gap-3 border border-gray-100">
                                            <p className="text-center text-xl text-gray-500">
                                                {event.date.slice(0,10)}
                                            </p>
                                            <p className="text-center text-xl text-gray-500">
                                                {event.location}
                                            </p>
                                            <p className="text-center text-xl text-gray-500">
                                                {event.description}
                                            </p>
                                        </div>


                                    </div>
                                    <ul
                                        role="list"
                                        className="mx-auto grid grid-cols-2 gap-x-4 gap-y-8 sm:grid-cols-4 md:gap-x-6 lg:max-w-5xl lg:gap-x-8 lg:gap-y-12 xl:grid-cols-6"
                                    >
                                        {people.map((person) => (
                                            <li key={person.name}>
                                                <div className="space-y-4">
                                                    <img className="mx-auto h-20 w-20 rounded-full lg:w-24 lg:h-24" src={person.imageUrl} alt="" />
                                                    <div className="space-y-2">
                                                        <div className="text-xs font-medium lg:text-sm">
                                                            <h3>{person.name}</h3>
                                                            <p className="text-indigo-600">{person.role}</p>
                                                        </div>
                                                    </div>
                                                </div>
                                            </li>
                                        ))}
                                    </ul>
                                </div>
                            </div>
                            {/* /End replace */}
                        </div>
                    </main>
                </div>
            </div>

    )
}
