import React, {useEffect} from 'react'
import {Transition} from "@headlessui/react";
import {Fragment} from "react";
import {CheckCircleIcon} from "@heroicons/react/24/outline";
import {XMarkIcon} from "@heroicons/react/24/solid";
import {useDispatch, useSelector} from "react-redux";
import {setNotificationOpen} from "../../state/NotificationSlice";
import {XCircleIcon} from "@heroicons/react/20/solid";

export const Notification = () => {

    const dispatch = useDispatch()
    const title = useSelector((state)=> state.notification.title)
    const type = useSelector((state)=> state.notification.type)
    const show = useSelector((state)=> state.notification.open)


    useEffect(()=>{
        setTimeout(()=>{
            dispatch(setNotificationOpen(false, false))
        },(5000))
    },[])


    return (
        <div
            aria-live="assertive"
            className="fixed inset-0 relative z-80 flex items-start px-4 py-6 pointer-events-none  sm:p-6 sm:items-start"
        >
            <div className="w-full flex flex-col items-center space-y-4 sm:items-end">
                {/* Notification panel, dynamically insert this into the live region when it needs to be displayed */}
                <Transition
                    show={show}
                    as={Fragment}
                    enter="transform ease-out duration-300 transition"
                    enterFrom="translate-y-2 opacity-0 sm:translate-y-0 sm:translate-x-2"
                    enterTo="translate-y-0 opacity-100 sm:translate-x-0"
                    leave="transition ease-in duration-100"
                    leaveFrom="opacity-100"
                    leaveTo="opacity-0"
                >
                    <div className="max-w-sm w-full bg-white shadow-lg rounded-lg pointer-events-auto ring-1 ring-black ring-opacity-5 overflow-hidden">
                        <div className="p-4">
                            <div className="flex items-start">
                                <div className="flex-shrink-0">
                                    {type ?
                                        (
                                            <CheckCircleIcon className="h-6 w-6 text-green-400" aria-hidden="true" />
                                        ) :
                                            (
                                        <XCircleIcon className="h-6 w-6 text-red-400" aria-hidden="true" />
                                        )}

                                </div>
                                <div className="ml-3 w-0 flex-1 pt-0.5">
                                    <p className="text-sm font-medium text-gray-900">{type ? ("Success"):("Failed")}</p>
                                    <p className="mt-1 text-sm text-gray-500">{title}</p>
                                </div>
                                <div className="ml-4 flex-shrink-0 flex">
                                    <button
                                        className="bg-white rounded-md inline-flex text-gray-400 hover:text-gray-500 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500"
                                        onClick={() => dispatch(setNotificationOpen(false, false))
                                        }
                                    >
                                        <span className="sr-only">Close</span>

                                        <XMarkIcon className="h-5 w-5" aria-hidden="true" />
                                    </button>
                                </div>
                            </div>
                        </div>
                    </div>
                </Transition>
            </div>
        </div>
    )
}
