import React, {useEffect, useState} from 'react'

import {Main} from "./Main";
import {Aside} from "./Aside";
import {AddEventModal} from "../../../components/modals/AddEventModal";
import {SideOverlay} from "../../../components/overlays/SideOverlay";
import {getUserEvents} from "../../../api/user";
import axios from "axios";




export const Events = () => {

    const [open, setOpen] = useState(false)
    const [formType, setFormType] = useState(true)
    const [events,setEvents] = useState([])



    const handleAddEvent = (e) =>{

        setFormType(true)
        setOpen(true)

    }
    const handleViewEvent = (e)=>{

        setFormType(false)
        setOpen(true)

    }


    useEffect(()=>{

         axios.get('/api/user/get-user-events')
            .then(res=>{
                if(res.status === 200){
                    //console.log("first res",res.data)

                   setEvents(res.data)

                }
                else{
                    return false
                }
            })
            .catch(error=>{
                console.log(error)
                return false
            })



    },[])


    return (
        <>
            <SideOverlay open={open} setOpen={setOpen} formType={formType} />
            <Main handleAddEvent={handleAddEvent} handleViewEvent={handleViewEvent}  events={events} />
        </>



    )
}
