import React, {Fragment, useContext, useEffect, useState} from 'react'
import {CogIcon, HomeIcon, UserGroupIcon, CalendarDaysIcon, PresentationChartBarIcon} from "@heroicons/react/24/outline";
import {MagnifyingGlassIcon} from "@heroicons/react/24/solid";
import {Menu, Transition} from "@headlessui/react";
import {DashSideNav} from "./DashSideNav";
import {DashMobileNav} from "./DashMobileNav";
import {DashTopbar} from "./DashTopbar";
import {Outlet, useNavigate} from "react-router-dom";
import {UserContext} from "../context/UserContext";

const path = window.location.pathname


const sidebarNavigation = [
    { name: 'Home', href: '/', icon: HomeIcon, current: path === '/' },
    { name: 'Events', href: '/events', icon: CalendarDaysIcon, current:  path === '/events' },
    { name: 'Clients', href: '/clients', icon: UserGroupIcon, current:  path === '/clients' },
    { name: 'Statistics', href: '/statistics', icon: PresentationChartBarIcon, current:  path === '/statistics' },
    { name: 'Settings', href: '/settings', icon: CogIcon, current:  path === '/settings' },
]

const userNavigation = [
    { name: 'Your Profile', href: '#' },
    { name: 'Sign out', href: '#' },
]

function classNames(...classes) {
    return classes.filter(Boolean).join(' ')
}

export const DashboardLayout = ({children}) => {

    const {user} = useContext(UserContext)
    const navigate = useNavigate()

    useEffect(()=>{

        if(!user){
            navigate('/login')
        }

    },[])

    const [mobileMenuOpen, setMobileMenuOpen] = useState(false)
    return (
        <div className="h-full flex">

            <DashSideNav classNames={classNames}
                         sidebarNavigation={sidebarNavigation}

            />
            <DashMobileNav classNames={classNames}
                           mobileMenuOpen={mobileMenuOpen}
                           setMobileMenuOpen={setMobileMenuOpen}
                           sidebarNavigation={sidebarNavigation}
            />


            <div className="flex-1 flex flex-col overflow-hidden">

                <DashTopbar
                classNames={classNames}
                setMobileMenuOpen={setMobileMenuOpen}
                userNavigation={userNavigation}
                />

                {/* Main content */}
                <div className="flex-1 flex items-stretch overflow-hidden">
                    <Outlet />
                </div>
            </div>
        </div>
    )
}
