import React, {useEffect, useMemo, useState} from 'react'

import axios from "axios";
import {Calendar} from "./Calendar";
import {SideOverlay} from "./SideOverlay";

const getEventsFromServer = async () =>{


    const events = await axios.get('/api/user/get-user-events')
        .catch(error=>{
            console.log(error)

        })

    const eventsMap = new Map();

    await events?.data?.forEach(event=> {

        const day = event?.date?.slice(9,10)
        const month = event?.date?.slice(6,7)
        const key = `${day}-${month}`

        const prevArray = eventsMap.get(key)

        if(!prevArray){
            eventsMap.set(key, [event])
        }else{
            prevArray.push(event)

        }
    })

    return eventsMap


}


const events = useMemo(()=> getEventsFromServer())



export const EventsPage = () => {


    const [open, setOpen] = useState(false)
    const [formType, setFormType] = useState(true)
    const [eventSelected, setEventSelected] = useState({})





    console.log("event page load")


    const handleAddEvent = (e) =>{

        console.log("handle add event click")
        setFormType(true)
        setOpen(true)

    }

    const handleViewEvent = async (e, event)=>{

        console.log("handle view event click")
        await setEventSelected(event)

        setFormType(false)
        setOpen(true)


    }




    return (

        <>

            <SideOverlay open={open} setOpen={setOpen} formType={formType} event={eventSelected}  />

            <main className="lg:py-8 lg:px-16 px-4 flex-1 overflow-y-auto">

                <section aria-labelledby="primary-heading" className="min-w-0 flex-1 h-full flex flex-col lg:order-last">

                        <Calendar handleAddEvent={handleAddEvent} handleViewEvent={handleViewEvent} events={events} />



                </section>
            </main>
        </>


    )
}
