import React, {useEffect, useMemo, useState} from 'react'

import {Main} from "./Main";
import {Aside} from "./Aside";
import {AddEventModal} from "../../../components/modals/AddEventModal";
import {SideOverlay} from "../../../components/overlays/SideOverlay";
import {getUserEvents} from "../../../api/user";
import axios from "axios";
import {useNavigate} from "react-router-dom";




export const Events = () => {

    const [open, setOpen] = useState(false)
    const [formType, setFormType] = useState(true)
    const [events,setEvents] = useState([])
    const [load, setLoad] = useState(false)
    const navigate = useNavigate()
    const [eventIdSelected, setEventIdSelected] = useState('')



    const handleAddEvent = (e,id) =>{

        //setEventIdSelected(id)
        setFormType(true)
        setOpen(true)

    }
    const handleViewEvent = (e, event)=>{

        console.log("handle", event)

        setEventIdSelected(event.id)
        setFormType(false)
        setOpen(true)

    }





    useEffect(()=>{

         axios.get('/api/user/get-user-events')
            .then(res=>{
                if(res.status === 200){
                    //console.log(res.data)
                   setEvents(res.data)
                    setLoad(true)

                }
                else{
                    console.log("failed to fetch events")
                }
            })
            .catch(error=>{
                console.log(error)
                return false
            })



    },[])



    return (

        <>

                <SideOverlay open={open} setOpen={setOpen} formType={formType} eventId={eventIdSelected}  />
                <Main handleAddEvent={handleAddEvent} handleViewEvent={handleViewEvent}  events={events} />
        </>


    )
}
