import React, {Fragment, useContext, useEffect, useState} from 'react'
import {CogIcon, HomeIcon, CalendarDaysIcon, PresentationChartBarIcon, UsersIcon, WifiIcon} from "@heroicons/react/24/outline";
import {MagnifyingGlassIcon} from "@heroicons/react/24/solid";
import {Menu, Transition} from "@headlessui/react";
import {DashSideNav} from "./DashSideNav";
import {DashMobileNav} from "./DashMobileNav";
import {DashTopbar} from "./DashTopbar";
import {Outlet, useNavigate} from "react-router-dom";
import {UserContext} from "../context/UserContext";
import {Notification} from "../components/overlays/Notification";

const path = window.location.pathname


const sidebarNavigation = [
    { name: 'Home', href: '/dashboard', icon: HomeIcon, current: path === '/dashboard' },
    { name: 'Events', href: '/dashboard/events', icon: CalendarDaysIcon, current:  path === '/dashboard/events' },
    { name: 'Statistics', href: '/dashboard/statistics', icon: PresentationChartBarIcon, current:  path === '/dashboard/statistics' },
    { name: 'Genres', href: '/dashboard/genres', icon: WifiIcon, current:  path === '/dashboard/genres' },
    { name: 'Profile', href: '/dashboard/profile', icon: UsersIcon, current:  path === '/dashboard/profile' },
    { name: 'Settings', href: '/dashboard/settings', icon: CogIcon, current:  path === '/dashboard/settings' },
]

const userNavigation = [
    { name: 'Your Profile', href: '/dashboard/profile' },
    { name: 'Sign out', href: '/dashboard/signout' },
]

function classNames(...classes) {
    return classes.filter(Boolean).join(' ')
}

export const DashboardLayout = ({children}) => {




    const [mobileMenuOpen, setMobileMenuOpen] = useState(false)
    const [showNotification, setShowNotification] = useState(true)
    const [notificationType, setNotificationType] = useState(true)
    const [notificationTitle , setNotificationTitle] = useState('')

    return (
        <div className="h-full flex">

            <DashSideNav classNames={classNames}
                         sidebarNavigation={sidebarNavigation}

            />
            <DashMobileNav classNames={classNames}
                           mobileMenuOpen={mobileMenuOpen}
                           setMobileMenuOpen={setMobileMenuOpen}
                           sidebarNavigation={sidebarNavigation}
            />


            <div className="flex-1 flex flex-col overflow-hidden">

                <DashTopbar
                classNames={classNames}
                setMobileMenuOpen={setMobileMenuOpen}
                userNavigation={userNavigation}
                />

                {/* Main content */}
                <div className="flex-1 flex items-stretch overflow-hidden">
                    <Outlet />
                </div>
            </div>

            <Notification show={showNotification} setShow={setShowNotification} title={notificationTitle} />
        </div>
    )
}
