import React, {useEffect, useRef, useState} from 'react'
import {
    ChevronLeftIcon,
    ChevronRightIcon,
    ClockIcon,
} from "@heroicons/react/24/solid";

import dayjs from "dayjs";



function classNames(...classes) {
    return classes.filter(Boolean).join(' ')
}

const userEvents = [
    { id: 1, name: 'Design review', time: '10AM', datetime: '2023-06-02T10:00', href: '#' },
    { id: 2, name: 'Sales meeting', time: '2PM', datetime: '2023-06-05T14:00', href: '#' },
]


export const Calendar = ({handleAddEvent, handleViewEvent}) => {

    const [isLoading, setIsLoading] = useState(false)
    const [selectedDay, setSelectedDay] = useState(null)
    const [eventSelected, setEventSelected] = useState(null)

    const now = dayjs()
    const dateFormat = "MMM YYYY";

    const [currentMonth, setCurrentMonth] = useState(now)
    const [days, setDays] = useState([])

    const handleNextMonth = () =>{

        const plus = currentMonth.add(1,"month");
        setCurrentMonth(plus);
    }

    const handlePrevMonth = () =>{

        const minus = currentMonth.subtract(1,"month");
        setCurrentMonth(minus);
    }

    const renderDays = async () =>{

        const newDays = []

        const dateFormat = "YYYY-MM-DD";

        const prevMonth = currentMonth.subtract(1, "month")
        const daysInPrevMonth = prevMonth.daysInMonth()
        const nextMonth = currentMonth.add(1, "month")

        const startOfMonth = currentMonth.startOf("month").day()
        const daysInCurrentMonth = currentMonth.daysInMonth()




        for(let i=daysInPrevMonth-startOfMonth+1; i<=daysInPrevMonth; i++){
            newDays.push({
                date: prevMonth.date(i).format(dateFormat),
                isCurrentMonth: false,
                isSelected: false,
                events: []
            })
        }

        for(let i=1; i<=daysInCurrentMonth; i++ ){
            newDays.push({
                date: currentMonth.date(i).format(dateFormat),
                isCurrentMonth: true,
                isToday: currentMonth.date(i).format(dateFormat) === now.format(dateFormat),
                isSelected: false,
                events: []
            })
        }

        const daysLeft = 42 - newDays.length

        for(let i=1; i<=daysLeft; i++ ){
            newDays.push({
                date: nextMonth.date(i).format(dateFormat),
                isCurrentMonth: false,
                isSelected: false,
                events: []
            })
        }

        //set events
        userEvents.forEach(event=>{
            newDays.forEach(day=>{
                if(event.datetime.slice(0,10) === day.date){
                    day.events.push(event)

                }
            })
        })

        setDays(newDays)


    }



    useEffect(()=>{
        setIsLoading(true)
        renderDays()
            .then(res=>{
