import axios from "axios";


export async function getUser(){
    await axios.get('/api/user/get-user')
        .then(res=>{
            if(res.status === 200){

                console.log("res",res)
                return res

            }
            else{
                return false
            }
        })
        .catch(error=>{
            console.log(error)
            return false
        })
}

export async function getUserEvents(){


    await axios.get('/api/user/get-user-events')
        .then(res=>{
            if(res.status === 200){
                //console.log(res.data)
                return res.data

            }
            else{
                return false
            }
        })
        .catch(error=>{
            console.log(error)
            return false
        })
}

export async function addUserEvent({name, location, date, description}){

    await axios.post('/api/user/add-event',{name, location, date, description})
        .then(res =>{

            if(res.status === 200){
                return true
            }else{
                return false
            }
        })
        .catch(error=>{
            console.log(error)
            return false
        })

}
